mapper.select:
SELECT *
FROM
(
    SELECT ROWNUM AS RNUM, A.*
    FROM
    (
        SELECT ADDRBOOK_NAME, ADDRBOOK_COMNAME, ADDRBOOK_TEL, ADDRBOOK_EMAIL,ADDRBOOK_DEPT,ADDRBOOK_RANK
        FROM ADDRESSBOOK
        ORDER BY ADDRBOOK_NO DESC
    )A
)

WHERE RNUM>#{firstRecordIndex}
	AND RNUM<=#{firstRecordIndex}+ #{recordCountPerPage}

mapper.update:
UPDATE MEMBER
SET MEM_NAME=#{memName}, MEM_HP1=#{memHp1}, MEM_HP2=#{memHp2}, MEM_HP3=#{memHp3},
	MEM_PAY=#{memPay}, MEM_HOLIDAY=#{memHoliday}, DEPT_NO=#{deptNo},
	POS_NO=#{posNo}, RANKS_NO=#{ranksNo}
WHERE MEM_NO=#{memNo}
