mapper.select:
SELECT *
FROM
(
    SELECT ROWNUM AS RNUM, A.*
    FROM
    (
        SELECT MEM_NO, MEM_NAME, DEPT_NO, POS_NO, MEM_HP1, MEM_HP2, MEM_HP3
        FROM MEMBER
        
        ORDER BY MEM_NO DESC
    )A
)

WHERE RNUM>#{firstRecordIndex}
	AND RNUM<=#{firstRecordIndex}+ #{recordCountPerPage}
